import React from 'react';
import '../scss/DigitalClock.scss'

const DigitalClock = ({hours, minutes, seconds}) => {
  console.log(hours)
  const timeDisplayFormatter = (hours) => {
    let count=1;
    for(let i = 13; i<=24;i++) {
      hours[i] = count++;
    }
  }
  return (
    <div className={"digital-clock"}>
      <section className="time">{timeDisplayFormatter}:{minutes}</section>
      <section className="seconds">{seconds}</section>
    </div>
  )
}

export default DigitalClock
