import React,{Component} from 'react';
import './Clock.scss';
import DigitalClock from './DigitalClock';
import AnalogClock from './AnalogClock';

export default class Clock extends Component {
  clockInterval = "";
  constructor(props) {
    super(props);
    this.handleDate = this.handleDate.bind(this);
    this.state = {
      hours: "",
      minutes: "",
      seconds: ""
    };
  }

  componentDidMount() {
    this.clockInterval = setInterval(this.handleDate, 1000);
  }

  componentWillUnmount() {
    clearInterval(this.clockInterval);
  }

  handleDate() {
    const { datediff } = this.props;
    const date = new Date();
    date.setHours(date.getHours() + datediff);
    let hours = this.formatTime(date.getHours());
    let minutes = this.formatTime(date.getMinutes());
    let seconds = this.formatTime(date.getSeconds());
    this.setState({ hours, minutes, seconds });
  }

  formatTime(time) {
    return time < 10 ? `0${time}` : time;
  }

  render() {
    const { hours, minutes, seconds } = this.state;
    const { title } = this.props;

    return (
      <div className="clock">
        <section className="clock">
          <h3>{title}</h3>

          <AnalogClock
            hours={hours}
            minutes={minutes}
            seconds={seconds}
          />
        </section>

        <section className="clock">
          <DigitalClock
            hours={hours}
            minutes={minutes}
            seconds={seconds}
          />
        </section>
      </div>
    );
  }
}
